function changeImageSrc(e){e.matches?document.querySelector(".hero-image").src="../images/hero-desktop.jpg":document.querySelector(".hero-image").src="../images/hero-mobile.jpg"}var matchMedia=window.matchMedia("(min-width: 768px)");function ValidateEmail(e){return!!e.value.match(/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/)}function emailValid(e){e.classList.contains("content__signin--invalid")&&e.classList.remove("content__signin--invalid")}function emailInvalid(e){return e.classList.contains("content__signin--invalid")||e.classList.add("content__signin--invalid"),!1}changeImageSrc(matchMedia),matchMedia.addListener(changeImageSrc);var form=document.querySelector(".content__signin");form.querySelector(".content__signin-input").addEventListener("blur",e=>{ValidateEmail(form.querySelector(".content__signin-input"))?emailValid(form):(e.preventDefault(),emailInvalid(form))}),form.addEventListener("submit",e=>{ValidateEmail(form.querySelector(".content__signin-input"))?emailValid(form):(e.preventDefault(),emailInvalid(form))});